import React , { Component }from 'react';
import './App.css';
import {fetchApi} from './apiCalls'


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      routes : []
    }
  }

  componentDidMount() {
    fetchApi('routes').then(routes => this.setState(routes))
  }

  render() {
    const route = this.state.routes
    return (
      <div className="App">
        <select className="select-box">
          <option>Select route</option>
          { route.map(route => {
            return <option>{route.discription}</option>
            })
          }
        </select>
      </div>
    );
  }

}

export default App;
