import React , { Component }from 'react';
import './App.css';
import {fetchApi} from './apiCalls'


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      routes : []
    }
  }

  componentDidMount() {
    fetchApi('routes').then(routes => this.setState({routes :routes }))
  }

  render() {
    const routeData = this.state.routes
    console.log(this.state)
    return (
      <div className="App">
        <select className="select-box">
          <option>Select route</option>
          { routeData.map(route => <option key={route.RouteId}>{route.Description}</option>)}
        </select>
      </div>
    );
  }

}

export default App;
