import React , { Component }from 'react';
import './App.css';
import {fetchApi} from './apiCalls'


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      routes : [],
      direction: [],
      stop: [],
      showDirection: false,
      showStop: false,
    }
  }

  componentDidMount() {
    fetchApi('routes').then(routes => this.setState({routes :routes }))
  }

  handleChange = (event) => {
    let value = event.target.value;
    console.log('value', value)
    if (value !== 'Select route') {
      this.setState({
          showDirection: true
      });
    } else {
      this.setState({
        showDirection: false
    });
    }
  }

  render() {
    const state = this.state
    const routeData = state.routes
    const directionData = state.direction
    const stopDate = state.stop
    return (
      <div className="App">
        <select className="select-box" onChange={(e) => this.handleChange(e)}>
          <option>Select route</option>
          { routeData.map(route => <option key={route.RouteId}>{route.Description}</option>)}
        </select>
        { state.showDirection &&
          <select className="select-box">
            <option>Select direction</option>
            { directionData.map(direction => <option key={direction.DirectionId}>{direction.DirectionName}</option>)}
          </select>
        }
        { state.showStop &&
          <select className="select-box">
            <option>Select stop</option>
            { stopDate.map(stop => <option key={stop.PlaceCode}>{stop.Description}</option>)}
          </select>
        }
      </div>
    );
  }

}

export default App;
