import React , { Component }from 'react';
import './App.css';
import {fetchApi} from './apiCalls'


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      routes : [],
      selectedRoute: '',
      direction: [],
      selectedDirection: '',
      stop: [],
      selectedStop: '',
      showDirection: false,
      showStop: false,
    }
  }

  componentDidMount() {
    fetchApi('routes').then(routes => this.setState({routes :routes }))
  }

  handleRoutChange = (event) => {
    let value = event.target.value;
    if (value !== 'Select route') {
      fetchApi('directions/'+value).then(direction => this.setState({direction :direction, showDirection: true, selectedRoute: value }))
    } else {
      this.setState({
        showDirection: false,
        direction: [],
        selectedRoute:''
    });
    }
  }

  handleDirectionChange = (event) => {
    let value = event.target.value;
    console.log('value', value)
    if (value !== 'Select route') {
      fetchApi('stops/'+this.state.selectedDirection+'/'+value).then(direction => this.setState({stop :direction, showStop: true }))
    } else {
      this.setState({
        showStop: false,
        stop: []
    });
    }
  }

  handleStopChange = (event) => {
    let value = event.target.value;
    console.log('value', value)
    if (value !== 'Select route') {
      fetchApi('directions/'+value).then(direction => this.setState({direction :direction }))
      this.setState({
          showDirection: true
      });
    } else {
      this.setState({
        showDirection: false,
        direction: []
    });
    }
  }

  render() {
    const state = this.state
    const routeData = state.routes
    const directionData = state.direction
    const stopDate = state.stop
    return (
      <div className="App">
        <select className="select-box" onChange={(e) => this.handleRoutChange(e)}>
          <option>Select route</option>
          { routeData.map(route => <option key={route.RouteId} value={route.RouteId}>{route.Description}</option>)}
        </select>
        { state.showDirection &&
          <select className="select-box" onChange={(e) => this.handleDirectionChange(e)}>
            <option>Select direction</option>
            { directionData.map(direction => <option key={direction.DirectionId} value={direction.DirectionId}>{direction.DirectionName}</option>)}
          </select>
        }
        { state.showStop &&
          <select className="select-box">
            <option>Select stop</option>
            { stopDate.map(stop => <option key={stop.PlaceCode}>{stop.Description}</option>)}
          </select>
        }
      </div>
    );
  }

}

export default App;
