const initialState = {
    routeData : [],
    selectedRoute: '',
    direction: [],
    selectedDirection: '',
    stop: [],
    selectedStop: '',
    showDirection: false,
    showStop: false,
    departureList: [],
    showDepartureList: false,
    showRouteSelection: true,
    showStopSelection: false
}

export default (state = initialState, action) => {
    switch (action.type) {
        case 'ONLOAD_ROUTE_ACTION':
         return {
            ...state,
          routeData: action.payload
         }
         case 'ROUTE_CHANGE_ACTION':
         return {
             ...state,
            showDirection: action.payload.showDirection,
            directionData: action.payload.direction
         }
        default:
         return state
    }
}
