export const onLoadRouteAction = (routes) => dispatch => {
    dispatch({
        type: 'ROUTE_ACTION',
        payload: {
            showDirection: false,
            direction: [],
            selectedRoute:'',
            showStop: false,
            departureList: [],
            showDepartureList: false,
          },
    })
}
