import React , { Component }from 'react';
import './App.css';
import {fetchApi} from './apiCalls'


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      routes : [],
      selectedRoute: '',
      direction: [],
      selectedDirection: '',
      stop: [],
      selectedStop: '',
      showDirection: false,
      showStop: false,
      departureList: [],
      showDepartureList: false,
      departureCountToShow: 3,
      depatureExpanded: false,
      showRouteSelection: true,
      showStopSelection: false
    }
  }

  componentDidMount() {
    fetchApi('routes').then(routes => this.setState({routes :routes }))
  }

  handleRoutChange = (event) => {
    let value = event.target.value;
    if (value !== 'Select route') {
      fetchApi('directions/'+value)
      .then(direction => this.setState({direction :direction, showDirection: true, selectedRoute: value }))
    } else {
      this.setState({
        showDirection: false,
        direction: [],
        selectedRoute:''
    })
    }
  }

  handleDirectionChange = (event) => {
    let value = event.target.value;
    if (value !== 'Select direction') {
      fetchApi('stops/'+this.state.selectedRoute+'/'+value)
      .then(direction => this.setState({stop :direction, showStop: true, selectedDirection: value }))
    } else {
      this.setState({
        showStop: false,
        stop: [],
        selectedDirection: ''
    })
    }
  }

  handleStopChange = (event) => {
    let value = event.target.value;
    if (value !== 'Select stop') {
      fetchApi(this.state.selectedRoute+'/'+this.state.selectedDirection+'/'+value)
      .then(departureList => this.setState({departureList: departureList, showDepartureList: true }))
    } else {
      this.setState({
        showDepartureList: false,
        departureList: []
    })
    }
  }

  showMoreDepature = () => {
    if(this.state.departureCountToShow === 3 ){
      this.setState({ departureCountToShow: this.state.departureList.Departures.length, depatureExpanded: true })
    } else {
      this.setState({ departureCountToShow: 3, depatureExpanded: false })
    }
  }

  onSelectRouteAndStop = (selected) => {
    if(selected === 'route' ){
      this.setState({ showRouteSelection: true, showStopSelection: false })
    } else if(selected === 'stop' ) {
      this.setState({ showRouteSelection: false, showStopSelection: true })
    }
  }

  render() {
    // const state = this.state
    // const routeData = state.routes
    // const directionData = state.direction
    // const stopData = state.stop
    // const departureList = state.departureList
    const { routeData ,directionData, stopData, departureList } = this.state
    return (
      <div className="App">
        <button className="button-main">By route</button>
        <button className="button-main">By stop #</button>
        <div className="select-box-container">
          <select className="select-box" onChange={(e) => this.handleRoutChange(e)}>
          <option>Select route</option>
          { routeData.map(route => <option key={route.RouteId} value={route.RouteId}>{route.Description}</option>)}
        </select>
        { state.showDirection &&
          <select className="select-box" onChange={(e) => this.handleDirectionChange(e)}>
            <option>Select direction</option>
            { directionData.map(direction => <option key={direction.DirectionId} value={direction.DirectionId}>{direction.DirectionName}</option>)}
          </select>
        }
        { state.showStop &&
          <select className="select-box" onChange={(e) => this.handleStopChange(e)}>
            <option>Select stop</option>
            { stopData.map(stop => <option key={stop.PlaceCode} value={stop.PlaceCode}>{stop.Description}</option>)}
          </select>
        }
        </div>
        {state.showDepartureList && 
          <div className="depature-container">
            <div className="depature">
                <h2 className="depature-title">Departures</h2>
                <div className="stop-description">
                  <div>{departureList.Stop.Description}</div>
                  <div>Stop {departureList.Stop.StopId}</div>
                </div>
            </div>
            <div>
              {departureList.Departures.slice(0, state.departureCountToShow).map(depature => {
                return <div className="depature-list" key={depature.BlockNumber}>
                  <div className="route-discription">
                    <span className="route-id"><strong>{depature.RouteId}</strong></span>
                    <span>{depature.Description}</span>
                  </div>
                  <div className="depart-time"><strong>{depature.DepartureText}</strong></div>
                  </div>
              })}
            </div>
            <button className="show-more-button" onClick={this.showMoreDepature}>
              <span className={state.depatureExpanded ? 'expand': 'colapsed'}></span>
              Show {state.depatureExpanded ? 'less' : 'more' } depature time
            </button>
          </div>
        }
      </div>
    );
  }
}

export default App;
