export default (state = {}, action) => {
    switch (action.type) {
        case 'ROUTE_ACTION':
         return {
          routeData: action.payload
         }
         case 'DIRECTION_ACTION':
         return {
          directionData: action.payload
         }
        default:
         return state
    }
}
